        const response = await axios.get(`http://localhost:3001/api/ships/${id}`);
        dispatch({type: SHIP_UPDATE_SUCCESS, data: response.data});
    } catch (error) {
        dispatch({type: SHIP_UPDATE_ERROR, error});
    }
}
