import React, { Component, useState } from 'react';
import { connect } from 'react-redux';
import _ from 'lodash';

import logo from './logo.svg';
import './App.css';
import Ship from './components/ship';
import {fetchShips} from './actions/shipListActions';

class App extends Component {
	state = {
		selected: null
	}

	setSelected = value => {
		this.setState({selected: value});
	}

	render() {
		const ships = _.get(this.props, 'ships.data', []);

		console.log('render() this.state.selected', this.state.selected);
		return (
			<div className="App">
				<pre>{JSON.stringify(this.props)}</pre>
				<pre>{JSON.stringify(this.state)}</pre>

				<button onClick={this.props.fetchShips}>Fetch Ships</button>
				 <h2>
	                List of Ships
	            </h2>
	            <ul>
	                {ships && ships.map(ship => (
	                	<li
	                		key={ship.id}
	                		onClick={() => this.setSelected(ship.id)}>
	                			{JSON.stringify(ship, null, 2)}
	                	</li>
	                ))}
	            </ul>

	            <div>
	            	<h3>Selected Ship</h3>
	            	<div>{JSON.stringify(this.props.selectedShip, null, 2)}</div>
	            </div>

	            <Ship shipId={this.state.selected} />
			</div>
		);
	}
}

const mapStateToProps = state => {
	return {
		ships: state.ships,
		selectedShip: state.selectedShip,
	}
}

const mapDispatchToProps = dispatch => ({
	fetchShips: () => dispatch(fetchShips())
})

export default connect(mapStateToProps, mapDispatchToProps)(App);
