const axios = require('axios');

export const SHIPS_FETCH_ONE_PENDING = 'SHIPS_FETCH_ONE_PENDING';
export const SHIPS_FETCH_ONE_ERROR = 'SHIPS_FETCH_ONE_ERROR';
export const SHIPS_FETCH_ONE_SUCCESS = 'SHIPS_FETCH_ONE_SUCCESS';

export const fetchShipById = id => async dispatch => {
    dispatch({type: SHIPS_FETCH_ONE_PENDING});
    try {
        const response = await axios.get(`http://localhost:3001/api/ships/${id}`);
        console.log('fetchShipById - response', response);
        dispatch({type: SHIPS_FETCH_ONE_ERROR, data: response.data});
    } catch (error) {
        dispatch({type: SHIPS_FETCH_ONE_SUCCESS, error});
    }
}
