import React, { useState } from 'react';
import { connect, useSelector } from "react-redux";
import _ from 'lodash';

const classifications =  ['Starfighter', 'Freighter', 'Capital'];

function Ship(id) {
    const ship = useSelector(state => state.selectedShip.data);
    const [error, setError] = useState({});
    const [name, setName] = useState(ship.name || '');
    const [classification, setClassification] = useState(ship.classification || '');
    const [positions, setPositions] = useState(ship.positions || []);

    const handleSave = (event, getShips) => {
        event.preventDefault();
        console.log('handleSave');

        const ship = {
            id,
            name,
            classification,
            positions,
        }
    }

    const shipId = 'New Ship';
    return (
        <div>
            {
                !_.isEmpty(error) && <span>
                    <h3>Error</h3>
                    {`${error.name} - ${error.message || error.errmsg}`}
                </span>
            }
            <h3>{JSON.stringify(id, null, 2)}</h3>
            <h3>{shipId}</h3>
            <div>
                <label for="name">Name:</label>
                <input type="text" id="name" value={name} onChange={e => setName(e.target.value)}/>
            </div>
            <div>
                <label for="classification">Classification:</label>
                <select value={classification} onChange={e => setName(e.target.value)}>
                    {classifications.map((entry) => (<option value={entry} key={entry}>{entry}</option>))}
                </select>
            </div>
            <div>
                <div>Positions:</div>
                <div>
                    {JSON.stringify(positions, null, 4)}
                </div>
            </div>
            <button onClick={e => handleSave}>Save</button>
        </div>
    );
}

export default Ship;
